import React, { useState } from 'react';
import Toggle from './ToggleRenderProps';
// import ReactPlayer from "react-player";
// import { FormCheckbox } from "shards-react";
// import { MDBContainer } from "mdbreact";

import { Row, Col, FormControl, Button } from 'react-bootstrap';

// import { MDBInput, MDBFormInline, MDBBtn } from "mdbreact";
import '@fortawesome/fontawesome-free/css/all.min.css';
import 'bootstrap-css-only/css/bootstrap.min.css';
import 'mdbreact/dist/css/mdb.css';

import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';

import './styles.css';

// import "katex/dist/katex.min.css";
// import { BlockMath } from "react-katex";

import {
  // numberFormat1,
  // numberFormat2,
  // numberFormat3,
  numberFormat4,
  //numberFormat5,
  //numberFormat6,
} from './lib'; //ændrer til komma og pct + DKK
import Container from 'react-bootstrap/Container';

import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
// import "bootstrap/dist/css/bootstrap.min.css";

import 'katex/dist/katex.min.css';
// import { BlockMath } from "react-katex";
require('highcharts/modules/exporting')(Highcharts);
require('highcharts/modules/annotations')(Highcharts);

require('highcharts/highcharts-more')(Highcharts);

export function Pris() {
  const [a, seta] = useState(+(-1).toFixed(2));
  const [b, setb] = useState(+(100).toFixed(2));
  const [p, setp] = useState(+(60).toFixed(2));

  // var x;
  // var dataoms = [];
  // for (var i = 0; i < 10; i += 0.1) {
  //   dataoms.push([x, fun(x)]);
  // }

  // function fun(x) {
  //   return a * x * x + b * x; // or other function
  // }
  var xomsmax = (-0.5 * b) / a;
  var omsmax = a * xomsmax ** 2 + b * xomsmax;
  var afsmax = b / -a;
  return (
    <div>
      <br></br>
      <Container>
        <div class="p-3 mb-2 bg-secondary text-white">
          {/* <div class="card">
            <div class="p-4"> */}
          <h3>P og punkt-priselasticiteten</h3>
          <h5>Hvilke afsætninger og priser passer sammen</h5>
        </div>
      </Container>

      <Container className="p-0">
        <div class="p-3 mb-2 bg-white text-black">
          <div class="card">
            <div class="card-body"></div>

            <Container>
              <div class="p-3 mb-2 mt-0 bg-white">
                <h5>
                  Prisafsætningsfunktionen P = aX+b = {numberFormat4(a)}X + {b}
                </h5>

                <Form>
                  <Row>
                    <Col>
                      <InputGroup size="sm">
                        {/* <InputGroup.Prepend>
                          <InputGroup.Text size="sm">i boks før input</InputGroup.Text>
                        </InputGroup.Prepend> */}
                        <FormControl
                          type="number"
                          max="-0.000000001"
                          step={0.01}
                          precision={0}
                          //mobile={true}
                          value={+a}
                          onChange={(e) => seta(-Math.abs(e.target.value))}
                          placeholder="0"
                        />

                        <InputGroup.Append>
                          <InputGroup.Text inputGroup-sizing-sm id="basic-addon2">
                            a
                          </InputGroup.Text>
                        </InputGroup.Append>
                      </InputGroup>
                      <Form.Text className="text-muted">Hældningskoefficienten: a</Form.Text>
                    </Col>
                    <Col>
                      <InputGroup size="sm">
                        {/* <InputGroup.Prepend>
                          <InputGroup.Text size="sm">i boks før input</InputGroup.Text>
                        </InputGroup.Prepend> */}
                        <Form.Control
                          type="number"
                          min="0"
                          step={1}
                          precision={0}
                          mobile={true}
                          value={+b}
                          onChange={(e) => setb(Math.abs(e.target.value))}
                          placeholder="0"
                        />
                        <InputGroup.Append>
                          <InputGroup.Text id="basic-addon2">b</InputGroup.Text>
                        </InputGroup.Append>
                      </InputGroup>
                      <Form.Text className="text-muted">Skæringen med y-aksen: b</Form.Text>
                    </Col>
                  </Row>
                  <h5>Pris</h5>
                  <Row>
                    <Col xs={6}>
                      <InputGroup size="sm">
                        {/* <InputGroup.Prepend>
                          <InputGroup.Text size="sm">i boks før input</InputGroup.Text>
                        </InputGroup.Prepend> */}
                        <FormControl
                          type="number"
                          min="0"
                          step={1}
                          precision={0}
                          //mobile={true}
                          value={+p}
                          onChange={(e) => setp(Math.abs(e.target.value))}
                          placeholder="0"
                        />

                        <InputGroup.Append>
                          <InputGroup.Text inputGroup-sizing-sm id="basic-addon2">
                            P
                          </InputGroup.Text>
                        </InputGroup.Append>
                      </InputGroup>
                      <Form.Text className="text-muted">Fastsat pris for varen: P</Form.Text>
                    </Col>
                    <col></col>
                  </Row>
                </Form>
              </div>
            </Container>
          </div>
        </div>
      </Container>

      <Container className="p-0">
        <div class="p-3 mb-2 bg-white text-black">
          <div class="card">
            <div class="card-body">
              <Toggle
                render={({ on, toggle }) => (
                  <div>
                    <Button size="sm" onClick={toggle}>
                      {on && 'Skjul priselasticitet'}
                      {!on && 'Vis priselasticitet'}
                    </Button>
                    {on && (
                      <HighchartsReact
                        // containerProps={{ style: { height: "200%" } }}
                        highcharts={Highcharts}
                        options={{
                          title: {
                            text: 'Pris- afsætningsfunktionen',
                          },
                          tooltip: {
                            enabled: true,
                          },
                          exporting: {
                            buttons: {
                              contextButton: {
                                menuItems: [
                                  'viewFullscreen',
                                  'separator',
                                  // "downloadPNG",
                                  'downloadPDF',
                                  // "downloadSVG",
                                ],
                              },
                            },
                          },
                          credits: {
                            enabled: false,
                          },
                          annotations: [
                            {
                              labelOptions: {
                                // backgroundColor: "rgba(252, 255, 197, 1)",
                                // verticalAlign: "top",
                                padding: 2,

                                style: {
                                  fontSize: '0.6em',
                                },
                              },
                              labels: [
                                {
                                  point: {
                                    xAxis: 0,
                                    yAxis: 0,
                                    x: (p - b) / a,
                                    y: p,
                                  },
                                  distance: 7,
                                  text:
                                    'Elasticiteten ved pris ' +
                                    numberFormat4(p) +
                                    ' DKK. bliver ' +
                                    numberFormat4(-p / (b - p)),
                                },
                                {
                                  point: {
                                    xAxis: 0,
                                    yAxis: 0,
                                    x: (-0.5 * b) / a,
                                    y: b / 2,
                                  },
                                  distance: 7,
                                  text: 'Enhedselasticitet = -1',
                                },
                                {
                                  point: {
                                    xAxis: 0,
                                    yAxis: 0,
                                    x: (-0.25 * b) / a,
                                    y: a * ((-0.25 * b) / a) + b,
                                  },
                                  distance: 7,
                                  text: 'Elastisk efterspørgsel < -1',
                                  borderWidth: 0,
                                  backgroundColor: 'green',
                                  style: { color: 'white' },
                                },
                                {
                                  // align: "right",
                                  // distance: 2,
                                  point: {
                                    xAxis: 0,
                                    yAxis: 0,
                                    x: 0,
                                    y: p + (b - p) * 0.5,
                                  },
                                  // x: 10,

                                  align: 'right',
                                  text: 'Øvre=' + numberFormat4(b - p),
                                  borderWidth: 0,
                                  backgroundColor: 'orange',
                                  style: { color: 'white' },
                                },
                                {
                                  point: {
                                    xAxis: 0,
                                    yAxis: 0,
                                    x: 0,
                                    y: p * 0.5,
                                  },
                                  // x: 3,

                                  text: 'Nedre=' + numberFormat4(p),
                                  borderWidth: 0,
                                  backgroundColor: 'yellow',
                                  style: { color: 'black' },
                                  // format: {
                                  //   overflow: "justify",
                                  // },
                                },
                                {
                                  point: {
                                    xAxis: 0,
                                    yAxis: 0,
                                    x: (-b / a - (p - b) / a) * 0.5 + (p - b) / a,
                                    y: 0,
                                  },
                                  distance: 10,

                                  text: 'Højre=' + numberFormat4(-b / a - (p - b) / a),
                                  borderWidth: 0,
                                  backgroundColor: 'purple',
                                  style: { color: 'white' },
                                },
                                {
                                  point: {
                                    xAxis: 0,
                                    yAxis: 0,
                                    x: ((p - b) / a) * 0.5,
                                    y: 0,
                                  },
                                  distance: 10,
                                  borderWidth: 0,
                                  backgroundColor: 'blue',
                                  style: { color: 'white' },
                                  text: 'Venstre=' + numberFormat4((p - b) / a),
                                },

                                {
                                  point: {
                                    xAxis: 0,
                                    yAxis: 0,
                                    x: (-0.75 * b) / a,
                                    y: a * ((-0.75 * b) / a) + b,
                                  },
                                  distance: 7,
                                  text: 'Uelastisk efterspørgsel mellem -1 og 0',
                                  borderWidth: 0,
                                  backgroundColor: 'red',
                                  style: { color: 'white' },
                                },
                                {
                                  point: {
                                    xAxis: 0,
                                    yAxis: 0,
                                    x: -b / a,
                                    y: 0,
                                  },
                                  distance: 7,
                                  text: 'P=' + numberFormat4(a) + 'X+' + numberFormat4(b),
                                },
                                {
                                  point: {
                                    xAxis: 0,
                                    yAxis: 0,
                                    x: (-0.5 * b) / a,
                                    y: 0,
                                  },
                                  distance: 10,
                                  text: 'GROMS=' + numberFormat4(2 * a) + 'X+' + numberFormat4(b),
                                },
                              ],
                            },
                            {
                              labelOptions: {
                                shape: 'connector',
                                align: 'right',
                                justify: false,
                                crop: true,
                                style: {
                                  fontSize: '0.8em',
                                  textOutline: '0px white',
                                },
                              },
                            },
                          ],
                          xAxis: {
                            title: {
                              text: 'Afsætning',
                            },
                            gridLineWidth: 1,
                            min: 0,
                            max: 1.1 * (-b / a),
                            labels: {
                              format: '{value} stk.',
                            },
                          },
                          yAxis: {
                            title: {
                              text: null,
                            },
                            min: 0,
                            max: b,
                            labels: {
                              format: '{value} DKK.',
                            },
                          },
                          series: [
                            // {
                            //   name: "TVO",
                            //   type: "polygon",
                            //   data: [
                            //     [0, 0],
                            //     [(p - b) / a, 0],
                            //     [(p - b) / a, p],
                            //     [0, p],
                            //   ],
                            //   color: "rgb(0,255,0,.5)",
                            // },
                            {
                              type: 'line',
                              marker: {
                                enabled: false,
                                symbol: 'circle',
                                radius: 2,
                              },
                              dashStyle: 'ShortDot',
                              name: 'Pris P',
                              data: [
                                [0, p],
                                [(p - b) / a, p],
                              ],
                            },
                            {
                              type: 'line',
                              color: 'red',
                              marker: {
                                enabled: false,
                                symbol: 'circle',
                                radius: 2,
                              },
                              dashStyle: 'ShortDot',
                              name: 'Mængde X',
                              data: [
                                [(p - b) / a, p],
                                [(p - b) / a, 0],
                              ],
                            },
                            {
                              type: 'line',
                              color: 'black',
                              marker: {
                                enabled: false,
                                symbol: 'circle',
                                radius: 2,
                              },
                              dashStyle: 'Dot',
                              name: 'Max omsætning',
                              data: [
                                [0, b / 2],
                                [(-0.5 * b) / a, b / 2],
                                [(-0.5 * b) / a, 0],
                              ],
                            },
                            {
                              type: 'line',
                              marker: {
                                enabled: false,
                                symbol: 'circle',
                                radius: 2,
                              },
                              name: 'GROMS',
                              data: [
                                [0, b],
                                [(-0.5 * b) / a, 0],
                              ],
                            },

                            {
                              type: 'line',
                              color: 'red',
                              lineWidth: 4,
                              marker: {
                                enabled: false,
                                symbol: 'circle',
                                radius: 0,
                              },
                              name: 'Uelastisk efterspørgsel',
                              data: [
                                [(-0.5 * b) / a, a * ((-0.5 * b) / a) + b],
                                [-b / a, 0],
                              ],
                            },
                            {
                              type: 'line',
                              color: 'green',
                              lineWidth: 4,
                              marker: {
                                // color: "blue",
                                enabled: false,
                                symbol: 'circle',
                                radius: 2,
                              },
                              name: 'Elastisk efterspørgsel',
                              data: [
                                [0, b],
                                [(-0.5 * b) / a, a * ((-0.5 * b) / a) + b],
                              ],
                            },
                            // {
                            //   type: "line",
                            //   lineWidth: 0,
                            //   marker: {
                            //     enabled: false,
                            //     symbol: "circle",
                            //     radius: 2,
                            //   },
                            //   name: "P",
                            //   data: [
                            //     [0, b],
                            //     [(-0.5 * b) / a, a * ((-0.5 * b) / a) + b],
                            //     [-b / a, 0],
                            //   ],
                            // },
                            {
                              type: 'line',
                              color: 'yellow',
                              lineWidth: 8,
                              marker: {
                                enabled: false,
                                symbol: 'circle',
                                radius: 0,
                              },
                              name: 'Nedre',
                              data: [
                                [0, p],
                                [0, 0],
                              ],
                            },
                            {
                              type: 'line',
                              color: 'orange',
                              lineWidth: 8,
                              marker: {
                                enabled: false,
                                symbol: 'circle',
                                radius: 0,
                              },
                              name: 'Øvre',
                              data: [
                                [0, p],
                                [0, b],
                              ],
                            },
                            {
                              type: 'line',
                              color: 'blue',
                              lineWidth: 8,
                              marker: {
                                enabled: false,
                                // symbol: "circle",
                                radius: 0,
                              },
                              name: 'Venstre',
                              data: [
                                [0, 0],
                                [(p - b) / a, 0],
                              ],
                            },
                            {
                              type: 'line',
                              color: 'purple',
                              lineWidth: 8,
                              marker: {
                                enabled: false,
                                // symbol: "circle",
                                radius: 0,
                              },
                              name: 'Højre',
                              data: [
                                [-b / a, 0],
                                [(p - b) / a, 0],
                              ],
                            },
                          ],
                        }}
                      />
                    )}

                    {!on && (
                      <HighchartsReact
                        // containerProps={{ style: { height: "200%" } }}
                        highcharts={Highcharts}
                        options={{
                          title: {
                            text: 'Pris- afsætningsfunktionen',
                          },
                          exporting: {
                            buttons: {
                              contextButton: {
                                menuItems: [
                                  'viewFullscreen',
                                  'separator',
                                  // "downloadPNG",
                                  'downloadPDF',
                                  // "downloadSVG",
                                ],
                              },
                            },
                          },
                          credits: {
                            enabled: false,
                          },
                          annotations: [
                            {
                              labelOptions: {
                                // backgroundColor: "rgba(252, 255, 197, 1)",
                                verticalAlign: 'top',
                                padding: 2,

                                style: {
                                  fontSize: '0.6em',
                                },
                              },
                              labels: [
                                {
                                  point: {
                                    xAxis: 0,
                                    yAxis: 0,
                                    x: (-0.48 * b) / a,
                                    y: 2 * a * ((-0.48 * b) / a) + b,
                                  },
                                  distance: 7,
                                  text: 'GROMS=' + numberFormat4(2 * a) + 'X+' + numberFormat4(b),
                                  borderWidth: 1,
                                  borderColor: 'blue',
                                  backgroundColor: 'blue',
                                  style: { color: 'white' },
                                },
                                {
                                  point: {
                                    xAxis: 0,
                                    yAxis: 0,
                                    x: (-0.85 * b) / a,
                                    y: a * ((-0.85 * b) / a) + b,
                                  },
                                  distance: 7,
                                  text: 'P=' + numberFormat4(a) + 'X+' + numberFormat4(b),

                                  borderWidth: 1,
                                  borderColor: 'green',
                                  backgroundColor: 'green',
                                  style: { color: 'white' },
                                },
                                {
                                  point: {
                                    xAxis: 0,
                                    yAxis: 0,
                                    x: (p - b) / a,
                                    y: p,
                                  },
                                  distance: 7,
                                  text:
                                    'Pris P = ' +
                                    +numberFormat4(p) +
                                    ' DKK.<br />Afsætning X = ' +
                                    numberFormat4((p - b) / a) +
                                    ' stk.',
                                  borderWidth: 1,
                                  borderColor: 'green',
                                  backgroundColor: 'green',
                                  style: { color: 'white' },
                                },
                                {
                                  point: {
                                    xAxis: 0,
                                    yAxis: 0,
                                    x: 0,
                                    y: b,
                                  },
                                  x: -4,
                                  // distance: -10,
                                  // align: "left",
                                  text:
                                    'Skæring med <br />y-aksen b = ' +
                                    +numberFormat4(b) +
                                    '<br />Max pris/betalingsvillighed',
                                  borderWidth: 1,
                                  borderColor: 'orange',
                                  backgroundColor: 'orange',
                                  style: { color: 'white' },
                                },
                                {
                                  point: {
                                    xAxis: 0,
                                    yAxis: 0,
                                    x: -b / a,
                                    y: 0,
                                  },
                                  // x: -4,
                                  distance: 7,
                                  // align: "left",
                                  text:
                                    'Skæring med <br />x-aksen -b/a = ' +
                                    numberFormat4(afsmax) +
                                    '<br />Max afsætning<br />markedsmætning',
                                  borderWidth: 1,
                                  borderColor: 'orange',
                                  backgroundColor: 'orange',
                                  style: { color: 'white' },
                                },
                                {
                                  point: {
                                    xAxis: 0,
                                    yAxis: 0,
                                    x: 0,
                                    y: b,
                                  },
                                  x: -4,
                                  // distance: -10,
                                  // align: "left",
                                  text:
                                    'Skæring med <br />y-aksen b = ' +
                                    +numberFormat4(b) +
                                    '<br />Max pris/betalingsvillighed',
                                  borderWidth: 1,
                                  borderColor: 'orange',
                                  backgroundColor: 'orange',
                                  style: { color: 'white' },
                                },
                                {
                                  point: {
                                    xAxis: 0,
                                    yAxis: 0,
                                    x: (b / 2 - b) / a,
                                    y: b / 2,
                                  },
                                  distance: -40,
                                  text:
                                    'Max omsætning ved<br />Pris P = ' +
                                    +numberFormat4(b / 2) +
                                    ' DKK.<br />Afsætning X = ' +
                                    numberFormat4((b / 2 - b) / a) +
                                    ' stk.',
                                  borderWidth: 1,
                                  borderColor: 'red',
                                  backgroundColor: 'red',
                                  style: { color: 'white' },
                                },
                              ],
                            },
                            {
                              labelOptions: {
                                shape: 'connector',
                                align: 'right',
                                justify: false,
                                crop: true,
                                style: {
                                  fontSize: '0.8em',
                                  textOutline: '0px white',
                                },
                              },
                            },
                          ],
                          xAxis: {
                            title: {
                              text: 'Afsætning',
                            },
                            gridLineWidth: 1,
                            min: 0,
                            max: 1.1 * (-b / a),
                            labels: {
                              format: '{value} stk.',
                            },
                          },
                          yAxis: {
                            title: {
                              text: null,
                            },
                            min: 0,
                            max: b,
                            labels: {
                              format: '{value} DKK.',
                            },
                          },
                          series: [
                            {
                              name: 'Omsætning',
                              type: 'polygon',
                              data: [
                                [0, 0],
                                [(p - b) / a, 0],
                                [(p - b) / a, p],
                                [0, p],
                              ],
                              color: 'rgb(0,255,0,.5)',
                            },
                            {
                              type: 'line',
                              marker: {
                                enabled: false,
                                symbol: 'circle',
                                radius: 2,
                              },
                              dashStyle: 'ShortDot',
                              name: 'Pris P',
                              data: [
                                [0, p],
                                [(p - b) / a, p],
                              ],
                            },
                            {
                              type: 'line',
                              color: 'red',
                              marker: {
                                enabled: false,
                                symbol: 'circle',
                                radius: 2,
                              },
                              dashStyle: 'ShortDot',
                              name: 'Mængde X',
                              data: [
                                [(p - b) / a, p],
                                [(p - b) / a, 0],
                              ],
                            },
                            {
                              type: 'line',
                              color: 'black',
                              marker: {
                                enabled: false,
                                symbol: 'circle',
                                radius: 2,
                              },
                              dashStyle: 'Dot',
                              name: 'Max omsætning',
                              data: [
                                [0, b / 2],
                                [(-0.5 * b) / a, b / 2],
                                [(-0.5 * b) / a, 0],
                              ],
                            },
                            {
                              type: 'line',
                              color: 'blue',
                              marker: {
                                enabled: false,
