import React, { useState, useEffect, useRef } from 'react';

import { Row, Col, FormControl, Alert } from 'react-bootstrap';
import 'chartjs-plugin-annotation';
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';

import './styles.css';
import {
  numberFormat1,
  // numberFormat2,
  numberFormat3,
  numberFormat4,
  //numberFormat5,
  //numberFormat6,
} from './lib'; //ændrer til komma og pct + DKK
import Container from 'react-bootstrap/Container';

import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import 'bootstrap/dist/css/bootstrap.min.css';

// import "katex/dist/katex.min.css";
// import { BlockMath } from "react-katex";
require('highcharts/modules/exporting')(Highcharts);
require('highcharts/modules/annotations')(Highcharts);
require('highcharts/highcharts-more')(Highcharts);

export function Optimering() {
  const [a, seta] = useState(+(-1.25).toFixed(2));
  const [b, setb] = useState(+(100).toFixed(2));
  const [ag, setag] = useState(+(0.5).toFixed(2));
  const [bg, setbg] = useState(+(50).toFixed(2));
  const [showAlert, setShowAlert] = useState(false);
  const alertRef = useRef(null);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (alertRef.current && !alertRef.current.contains(event.target)) {
        setShowAlert(false);
      }
    };

    if (showAlert) {
      document.addEventListener('mousedown', handleClickOutside);
    }

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [showAlert]);

  var ox;
  if (ag < 0 && -bg / (2 * ag) < -b / (2 * a)) {
    ox = -b / (2 * a);
  } else {
    ox = (bg - b) / (2 * a - ag);
  }

  var op = a * ox + b;
  var ove = 0.5 * ag * ox + bg;
  // var ogroms = ag * ox + bg;

  var dbneg;
  if ((a * ox + b) * ox - (0.5 * ag * ox + bg) * ox < 0) {
    dbneg = 'Bemærk! Dækningsbidraget er negativt, der bør slet ikke afsættes. ';
  } else {
    dbneg = '';
  }
  var xneg;

  if (ox < 0) {
    xneg = 'Bemærk! Afsætningen beregnes til negativ, dette er ikke muligt. Der bør slet ikke afsættes. ';
  } else {
    xneg = '';
  }

  var omsneg;
  if ((a * ox + b) * ox < 0 < 0) {
    omsneg = 'Bemærk! Omsætningen beregnes som negativ, der bør slet ikke afsættes. ';
  } else {
    omsneg = '';
  }

  var optimalx = ox.toFixed(2);
  var optimalp = op.toFixed(2);

  // var omsætning = (ox * op).toFixed(2);
  // var tvo = ox * (0.5 * ag * ox + bg);
  // var db = omsætning - tvo;
  // korrektion når AVC og GROMK konstatn
  var avcligning;
  if (ag !== 0) {
    avcligning = numberFormat4(0.5 * ag).concat('X + ', bg);
  } else {
    avcligning = bg;
  }

  var avcligning2;
  if (ag !== 0) {
    avcligning2 = '';
  } else {
    avcligning2 = ' = ' + bg;
  }

  var gromkligning;
  if (ag !== 0) {
    gromkligning = numberFormat4(ag).concat('X + ', bg);
  } else {
    gromkligning = bg;
  }

  var gromkligning2;
  if (ag !== 0) {
    gromkligning2 = '';
  } else {
    gromkligning2 = ' = ' + bg;
  }

  var tvoligning2;
  if (ag !== 0) {
    tvoligning2 = '';
  } else {
    tvoligning2 = ' = ' + bg + 'X';
  }

  return (
    <div>
      <br></br>
      <Container>
        <div class="p-3 mb-2 bg-secondary text-light">
          <h3>Optimering basis</h3>
          <h5>Maksimering af dækningsbidrag</h5>
          <h5>Bestem den optimale pris og afsætning</h5>
        </div>
      </Container>

      <Container className="p-0">
        <div class="p-3 mb-2 bg-white text-black">
          <div class="card">
            <div class="card-body"></div>

            <Container>
              <div class="p-3 mb-2 mt-0 bg-white">
                <h5>
                  Prisafsætningsfunktionen P = aX+b = {numberFormat4(a)}X + {b}
                </h5>

                <Form>
                  <Row>
                    <Col>
                      <InputGroup size="sm">
                        <FormControl
                          type="number"
                          step={0.01}
                          precision={0}
                          mobile={true}
                          value={a}
                          onChange={(e) => {
                            const value = e.target.value;
                            if (value === '') {
                              seta('');
                            } else {
                              const numValue = parseFloat(value);
                              if (numValue > 0) {
                                setShowAlert(true);
                                seta('');
                              } else {
                                seta(numValue);
                              }
                            }
                          }}
                          placeholder="0"
                        />
                        <InputGroup.Append>
                          <InputGroup.Text inputGroup-sizing-sm id="basic-addon2">
                            a
                          </InputGroup.Text>
                        </InputGroup.Append>
                      </InputGroup>
                      {showAlert && (
                        <Alert 
                          ref={alertRef}
                          variant="danger" 
                          style={{ 
                            backgroundColor: 'rgba(220, 53, 69, 0.2)',
                            border: '1px solid rgba(220, 53, 69, 0.3)',
                            color: '#721c24',
                            marginTop: '5px'
                          }}
                        >
                          Pas på hældningskoefficienten for P er aldrig positiv
                        </Alert>
                      )}
                    </Col>
                    <Col>
                      <InputGroup size="sm">
                        <Form.Control
                          type="number"
                          min="0"
                          step={1}
                          precision={0}
                          mobile={true}
                          value={+b}
                          onChange={(e) => setb(Math.abs(e.target.value))}
                          placeholder="0"
                        />
                        <InputGroup.Append>
                          <InputGroup.Text id="basic-addon2">b</InputGroup.Text>
                        </InputGroup.Append>
                      </InputGroup>
                      <Form.Text className="text-muted">Skæringen med y-aksen: b</Form.Text>
                    </Col>
                  </Row>
                </Form>
                <br />
                <h5>Grænseomkostningerne GROMK = aX+b = {gromkligning}</h5>

                <Form>
                  <Row>
                    <Col>
                      <InputGroup size="sm">
                        <Form.Control
                          type="number"
                          step={0.01}
                          precision={0}
                          mobile={true}
                          value={ag}
                          onChange={(e) => setag(e.target.value)}
                          aria-describedby="inputGroupAppend"
                          placeholder="0"
                        />
                        <InputGroup.Append>
                          <InputGroup.Text inputGroup-sizing-sm id="basic-addon2">
                            a
                          </InputGroup.Text>
                        </InputGroup.Append>
                      </InputGroup>
                      <Form.Text className="text-muted">Hældningskoefficienten: a</Form.Text>
                    </Col>
                    <Col>
                      <InputGroup size="sm">
                        <Form.Control
                          type="number"
                          min="0"
                          step={1}
                          precision={0}
                          mobile={true}
                          value={bg}
                          onChange={(e) => setbg(Math.abs(e.target.value))}
                          aria-describedby="inputGroupAppend"
                          placeholder="0"
                        />
                        <InputGroup.Append>
                          <InputGroup.Text id="basic-addon2">b</InputGroup.Text>
                        </InputGroup.Append>
                      </InputGroup>
                      <Form.Text className="text-muted">Skæringen med y-aksen: b</Form.Text>
                    </Col>
                  </Row>
                </Form>
              </div>
            </Container>
          </div>
        </div>
      </Container>

      <Container className="p-0">
        <div class="p-3 mb-2 bg-white text-black">
          <div class="card">
            <div class="card-body">
              <HighchartsReact
                highcharts={Highcharts}
                options={{
                  title: {
                    text: 'Optimering',
                  },
                  exporting: {
                    buttons: {
                      contextButton: {
                        menuItems: [
                          'viewFullscreen',
                          'separator',
                          // "downloadPNG",
                          'downloadPDF',
                          // "downloadSVG",
                        ],
                      },
                    },
                  },
                  credits: {
                    enabled: false,
                  },
                  annotations: [
                    {
                      labelOptions: {
                        verticalAlign: 'top',
                        padding: 2,

                        style: {
                          fontSize: '0.6em',
                        },
                      },
                      labels: [
                        {
                          point: {
                            xAxis: 0,
                            yAxis: 0,
                            x: (-0.5 * b) / a,
                            y: 2,
                          },
                          text: 'GROMS=' + numberFormat4(2 * a) + 'X+' + numberFormat4(b),
                          borderWidth: 1,
                          borderColor: 'blue',
                          backgroundColor: 'blue',
                          style: { color: 'white' },
                        },
                        {
                          point: {
                            xAxis: 0,
                            yAxis: 0,
                            x: -b / a,
                            y: 2,
                          },
                          text: 'P=' + numberFormat4(a) + 'X+' + numberFormat4(b),
                          borderWidth: 1,
                          borderColor: 'green',
                          backgroundColor: 'green',
                          style: { color: 'white' },
                        },

                        {
                          point: {
                            xAxis: 0,
                            yAxis: 0,
                            x: -b / a,
                            y: 0.5 * ag * (-b / a) + bg,
                          },
                          text: 'VE=' + avcligning,
                          borderWidth: 1,
                          borderColor: 'grey',
                          backgroundColor: 'grey',
                          style: { color: 'white' },
                        },
                        {
                          point: {
                            xAxis: 0,
                            yAxis: 0,
                            x: (-0.5 * b) / a,
                            y: ag * ((-0.5 * b) / a) + bg,
                          },
                          text: 'GROMK=' + gromkligning,
                          borderWidth: 1,
                          borderColor: 'red',
                          backgroundColor: 'red',
                          style: { color: 'white' },
                        },
                      ],
                    },
                    {
                      labelOptions: {
                        shape: 'connector',
                        align: 'right',
                        justify: false,
                        crop: true,
                        style: {
                          fontSize: '0.8em',
                          textOutline: '0px white',
                        },
                      },
                      labels: [
                        {
                          point: {
                            xAxis: 0,
                            yAxis: 0,
                            x: ox,
                            y: 0,
                          },
                          text: 'Optimal X<br>' + numberFormat3(ox) + 'stk.',
                        },
                        {
                          point: {
                            xAxis: 0,
                            yAxis: 0,
                            x: 0,
                            y: op,
                          },
                          text: 'Optimal P<br>' + numberFormat3(op) + 'kr.',
                        },
                      ],
                    },
                  ],
                  xAxis: {
                    title: {
                      text: 'Afsætning',
                    },
                    gridLineWidth: 1,
                    min: 0,
                    max: 1.1 * (-b / a),
                    labels: {
                      format: '{value} stk.',
                    },
                  },
                  yAxis: {
                    title: {
                      text: null,
                    },
                    min: 0,
                    max: Math.max(b, 0.5 * ag * (-b / a) + bg),
                    labels: {
                      format: '{value} DKK.',
                    },
                  },
                  series: [
                    {
                      name: 'TVO',
                      type: 'polygon',
                      data: [
                        [0, 0],
                        [+ox.toFixed(2), 0],
                        [+ox.toFixed(2), +ove.toFixed(2)],
                        [0, +ove.toFixed(2)],
                      ],
                      color: 'rgb(255,0,0,.2)',
                    },
                    {
                      name: 'DB',
                      type: 'polygon',
                      data: [
                        [0, ove],
                        [ox, ove],
                        [ox, op],
                        [0, op],
                      ],
                      color: 'rgb(0,255,0,.5)',
                    },
                    {
                      type: 'line',
                      color: 'blue',
                      marker: {
                        enabled: false,
                        symbol: 'circle',
                        radius: 2,
                      },
                      name: 'GROMS',
                      data: [
                        [0, b],
                        [-b / a, 0],
                      ],
                    },
                    {
                      marker: {
                        enabled: false,
                        symbol: 'circle',
                        radius: 3,
                      },
                      name: 'Optimal P',
                      data: [[ox.toFixed(2), +op.toFixed(2)]],
                    },
                    // {
                    //   marker: {
                    //     enabled: false,
                    //     symbol: "circle",
                    //     radius: 3,
                    //   },
                    //   name: "Optimal VE",
