import React, { useState } from "react";
import { Row, Col, FormControl } from "react-bootstrap";
import "@fortawesome/fontawesome-free/css/all.min.css";
import "bootstrap-css-only/css/bootstrap.min.css";
import "mdbreact/dist/css/mdb.css";
import Highcharts from "highcharts";
import HighchartsReact from "highcharts-react-official";
import "./styles.css";
import "katex/dist/katex.min.css";

import { numberFormat4 } from "./lib"; //ændrer til komma og pct + DKK
import Container from "react-bootstrap/Container";

import Form from "react-bootstrap/Form";
import InputGroup from "react-bootstrap/InputGroup";
// import "bootstrap/dist/css/bootstrap.min.css";

import "katex/dist/katex.min.css";
// import { BlockMath } from "react-katex";
require("highcharts/modules/exporting")(Highcharts);
require("highcharts/modules/annotations")(Highcharts);

require("highcharts/highcharts-more")(Highcharts);

export function ligninger() {
  const [a, seta] = useState(+(-2).toFixed(2));
  const [b, setb] = useState(+(100).toFixed(2));
  const [a2, seta2] = useState(+(1).toFixed(2));
  const [b2, setb2] = useState(+(50).toFixed(2));

  var dataline, dataline2, xmin, xmax, ymin, ymax, xskær, yskær;

  var textY1, textY2, anno;

  if (+a !== +a2) {
    xskær = (+b2 - +b) / (+a - a2);
    xskær = +xskær.toFixed(2);
    yskær = +a * +xskær + +b;
    yskær = +yskær.toFixed(2);
    xmin = Math.min(+xskær * 2, 0, -b * 2, -b2 * 2, -a * 2, -a2 * 2);
    xmax = Math.max(+xskær * 2, 0, b * 2, b2 * 2, a * 2, a2 * 2);
    ymin = Math.min(+yskær * 2, +b, +b2, -a * 2, -a2 * 2, 0, -b * 2, -b2 * 2);
    ymax = Math.max(+yskær * 2, +b, +b2, a * 2, a2 * 2, 0, +b * 2, +b2 * 2);
  } else {
    xmin = Math.min(-b * 2, -b2 * 2, -a * 2, -a2 * 2, 0);
    xmax = Math.max(b * 2, b2 * 2, a * 2, a2 * 2, 0);
    ymin = Math.min(-a * 2, -a2 * 2, 0, -b * 2, -b2 * 2);
    ymax = Math.max(a * 2, a2 * 2, 0, +b * 2, +b2 * 2);
  }
  dataline = [
    [+xmin * 10, +a * xmin * 10 + +b],
    [+xmax * 10, +a * xmax * 10 + +b],
  ];
  dataline2 = [
    [+xmin * 10, a2 * xmin * 10 + +b2],
    [+xmax * 10, a2 * xmax * 10 + +b2],
  ];

  if (+b > 0) {
    if (+a !== 0 && +a !== 1 && +a !== -1) {
      textY1 = numberFormat4(a) + "X + " + numberFormat4(+b);
    } else if (+a === 0) {
      textY1 = numberFormat4(+b);
    } else if (+a === 1) {
      textY1 = "X + " + numberFormat4(+b);
    } else {
      textY1 = "-X + " + numberFormat4(+b);
    }
  } else if (+b < 0) {
    if (+a !== 0 && +a !== 1 && +a !== -1) {
      textY1 = numberFormat4(a) + "X " + numberFormat4(+b);
    } else if (+a === 0) {
      textY1 = numberFormat4(+b);
    } else if (+a === 1) {
      textY1 = "X " + numberFormat4(+b);
    } else {
      textY1 = "-X " + numberFormat4(+b);
    }
  } else {
    if (+a !== 0) {
      textY1 = numberFormat4(a) + "X";
    } else {
      textY1 = numberFormat4(+b);
    }
  }

  if (+b2 > 0) {
    if (+a2 !== 0 && +a2 !== 1 && +a2 !== -1) {
      textY2 = numberFormat4(a2) + "X + " + numberFormat4(+b2);
    } else if (+a2 === 0) {
      textY2 = numberFormat4(+b2);
    } else if (+a2 === 1) {
      textY2 = "X + " + numberFormat4(+b2);
    } else {
      textY2 = "-X + " + numberFormat4(+b2);
    }
  } else if (+b2 < 0) {
    if (+a2 !== 0 && +a2 !== 1 && +a2 !== -1) {
      textY2 = numberFormat4(+a2) + "X " + numberFormat4(+b2);
    } else if (+a2 === 0) {
      textY2 = numberFormat4(+b2);
    } else if (+a2 === 1) {
      textY2 = "X " + numberFormat4(+b2);
    } else {
      textY2 = "-X " + numberFormat4(+b2);
    }
  } else {
    if (+a2 !== 0) {
      textY2 = numberFormat4(+a2) + "X";
    } else {
      textY2 = numberFormat4(+b2);
    }
  }

  anno = {
    labelOptions: {
      // verticalAlign: "top",
      padding: 1,

      style: {
        fontSize: "0.6em",
      },
    },
    labels: [
      {
        useHTML: true,
        point: {
          align: "left",
          xAxis: 0,
          yAxis: 0,
          x: (xmax + xmin) / 8,
          y: (a * (xmax + xmin)) / 8 + +b,
        },

        text: "Y<sub>1</sub>=" + textY1,

        borderWidth: 1,
        borderColor: "blue",
        backgroundColor: "blue",
        style: { color: "white" },
      },
      {
        useHTML: true,
        point: {
          align: "left",
          xAxis: 0,
          yAxis: 0,
          x: (xmax + xmin) / 6,
          y: (a2 * (xmax + xmin)) / 6 + +b2,
        },

        text: "Y<sub>1</sub>=" + textY2,

        borderWidth: 1,
        borderColor: "red",
        backgroundColor: "red",
        style: { color: "white" },
      },
    ],
  };

  return (
    <div>
      <br></br>
      <Container>
        <div class="p-3 mb-2 bg-secondary text-white">
          {/* <div class="card">
            <div class="p-4"> */}
          <h3>Skæring mellem 2 linjer</h3>
          <h5>Løsning af 2 ligninger</h5>
        </div>
      </Container>

      <Container className="p-0">
        <div class="p-3 mb-2 b2-white">
          <div class="card">
            <div class="card-body">
              <div class="p-3 mb-2 bg-white">
                <Form>
                  <h6>
                    Y<sub>1</sub> = {textY1}
                  </h6>
                  <Row>
                    <Col>
                      <InputGroup size="sm">
                        {/* <InputGroup.Prepend>
                          <InputGroup.Text size="sm">i boks før input</InputGroup.Text>
                        </InputGroup.Prepend> */}
                        <FormControl
                          type="number"
                          //   max="-0.000000001"
                          step={1}
                          precision={0}
                          //mobile={true}
                          value={+a}
                          onChange={(e) => seta(e.target.value)}
                          placeholder="0"
                        />

                        <InputGroup.Append>
                          <InputGroup.Text
                            inputGroup-sizing-sm
                            id="basic-addon2"
                          >
                            a
                          </InputGroup.Text>
                        </InputGroup.Append>
                      </InputGroup>
                      <Form.Text className="text-muted">
                        Hældningskoefficienten: a
                      </Form.Text>
                    </Col>
                    <Col>
                      <InputGroup size="sm">
                        {/* <InputGroup.Prepend>
                          <InputGroup.Text size="sm">i boks før input</InputGroup.Text>
                        </InputGroup.Prepend> */}
                        <Form.Control
                          type="number"
                          //   min="0"
                          step={1}
                          precision={0}
                          //   mobile={true}
                          value={+b}
                          onChange={(e) => setb(e.target.value)}
                          placeholder="0"
                        />
                        <InputGroup.Append>
                          <InputGroup.Text id="basic-addon2">b</InputGroup.Text>
                        </InputGroup.Append>
                      </InputGroup>
                      <Form.Text className="text-muted">
                        Skæringen med y-aksen: b
                      </Form.Text>
                    </Col>
                  </Row>
                  <h6>
                    Y<sub>2</sub> = {textY2}
                  </h6>
                  <Row>
                    <Col>
                      <InputGroup size="sm">
                        {/* <InputGroup.Prepend>
                          <InputGroup.Text size="sm">i boks før input</InputGroup.Text>
                        </InputGroup.Prepend> */}
                        <FormControl
                          type="number"
                          //   max="-0.000000001"
                          step={1}
                          precision={0}
                          //mobile={true}
                          value={+a2}
                          onChange={(e) => seta2(e.target.value)}
                          placeholder="0"
                        />

                        <InputGroup.Append>
                          <InputGroup.Text
                            inputGroup-sizing-sm
                            id="basic-addon2"
                          >
                            a
                          </InputGroup.Text>
                        </InputGroup.Append>
                      </InputGroup>
                      <Form.Text className="text-muted">
                        Hældningskoefficienten: a
                      </Form.Text>
                    </Col>
                    <Col>
                      <InputGroup size="sm">
                        {/* <InputGroup.Prepend>
                          <InputGroup.Text size="sm">i boks før input</InputGroup.Text>
                        </InputGroup.Prepend> */}
                        <Form.Control
                          type="number"
                          //   min="0"
                          step={1}
                          precision={0}
                          mobile={true}
                          value={+b2}
                          onChange={(e) => setb2(e.target.value)}
                          placeholder="0"
                        />
                        <InputGroup.Append>
                          <InputGroup.Text id="basic-addon2">b</InputGroup.Text>
                        </InputGroup.Append>
                      </InputGroup>
                      <Form.Text className="text-muted">
                        Skæringen med y-aksen: b
                      </Form.Text>
                    </Col>
                  </Row>
                </Form>
              </div>
            </div>
          </div>
        </div>
      </Container>

      <Container className="p-0">
        <div class="p-3 mb-2 bg-white text-black">
          <div class="card">
            <div class="card-body">
              <div>
                <HighchartsReact
                  highcharts={Highcharts}
                  options={{
                    title: {
                      text:
                        "Skæring  = (" +
                        numberFormat4(xskær) +
                        "," +
                        numberFormat4(yskær) +
                        ")",
                    },
                    exporting: {
                      buttons: {
                        contextButton: {
                          menuItems: [
                            "viewFullscreen",
                            "separator",
                            // "downloadPNG",
                            "downloadPDF",
                            // "downloadSVG",
                          ],
                        },
                      },
                    },
                    credits: {
                      enabled: false,
                    },
