import React, { useState, useEffect } from 'react';
import Toggle from './ToggleRenderProps';

import { Row, Col, FormControl, Button } from 'react-bootstrap';
import 'chartjs-plugin-annotation';
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';

import './styles.css';
import {
  numberFormat1,
  numberFormat2,
  numberFormat3,
  numberFormat4,
  numberFormat5,
  numberFormat6,
} from './lib';
import Container from 'react-bootstrap/Container';

import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';

import 'bootstrap/dist/css/bootstrap.min.css';

// Import modules separately - DO NOT initialize yet
import exporting from 'highcharts/modules/exporting';
import annotations from 'highcharts/modules/annotations';
import highchartsMore from 'highcharts/highcharts-more';

export function optimering2() {
  // Initialize modules inside useEffect ONCE
  useEffect(() => {
    console.log("Initializing Highcharts modules...");
    exporting(Highcharts);      // Initialize exporting module
    annotations(Highcharts);    // Initialize annotations module
    highchartsMore(Highcharts); // Initialize highcharts-more

    // If pattern-fill-v2 is imported, initialize it here too
    // patternFill(Highcharts); // Example

    // Or if pattern-fill is loaded via a script tag, try accessing Highcharts
    // This is less ideal but might be necessary depending on how pattern-fill works
    if (typeof window.PatternFill !== 'undefined') {
        console.log("Attempting to initialize pattern-fill globally?");
        // You might need a specific initialization method for pattern-fill
    }

    console.log("Highcharts modules initialized.");

  }, []); // Empty dependency array ensures this runs only once

  // --- State Variables ---
  const [a, seta] = useState(+(-1).toFixed(2));
  const [aop, setaop] = useState(+(-0.5).toFixed(2));
  const [bop, setbop] = useState(+(100).toFixed(2));
  const [aned, setaned] = useState(+(-1).toFixed(2));
  const [bned, setbned] = useState(+(125).toFixed(2));
  const [knækmængde, setknækmængde] = useState(+Infinity);
  const [ordre, setordre] = useState(+(20).toFixed(2));
  const [ordrepris, setordrepris] = useState(+(64).toFixed(2));
  const [ordremængde, setordremængde] = useState(+Infinity);
  const [b, setb] = useState(+(100).toFixed(2));
  const [ag, setag] = useState(+(0.5).toFixed(2));
  const [bg, setbg] = useState(+(50).toFixed(2));
  const [gskift, setgskift] = useState(+Infinity);
  const [ag2, setag2] = useState(+(0).toFixed(2));
  const [bg2, setbg2] = useState(+(0).toFixed(2));
  const [kap, setkap] = useState(+Infinity);
  const [reklame, setreklame] = useState(+(0).toFixed(2));

  // --- Derived State / Calculation Variables ---
  // Note: These calculations need to happen *before* they are used in chartOptions
  // Recalculate knækmængde based on aop, bop, aned, bned
  // This should ideally be in a useEffect hook that depends on those states,
  // but for now, we calculate it directly here.
  let derivedKnækmængde = Infinity;
  if (Math.abs(aop - aned) > 1e-9) {
      derivedKnækmængde = (bned - bop) / (aop - aned);
  }

  let knækpris = aop * derivedKnækmængde + bop;
  let knækgromsop = 2 * aop * derivedKnækmængde + bop;
  let knækgromsned = 2 * aned * derivedKnækmængde + bned;

  let currentA = a;
  if (reklame !== 0) {
    currentA = a / (1 + reklame / 100);
  }

  // Determine scenario based on toggles (this is implicitly done by your Toggle components)
  // We need these flags *before* calculating chartOptions
  // THIS IS A SIMPLIFICATION - Your Toggle logic actually sets the state variables
  // like knækmængde, ordremængde, gskift, kap, reklame. We use these state vars directly.
  const isOligopoly = knækmængde !== Infinity;
  const hasOrder = ordremængde !== Infinity;
  const hasGskift = gskift !== Infinity;
  const hasKap = kap !== Infinity;
  const hasReklame = reklame !== 0;

  // --- Optimal Point Calculation (Simplified - needs full logic) ---
  // Your complex ox1, ox2, ox, etc. calculations go here
  // ... (Your existing ~200 lines of ox/op calculation logic) ...
  let ox = 0; // Placeholder
  let op = 0; // Placeholder
  let dbfinal = 0; // Placeholder
  let optimalx = 0; // Placeholder from your original code?
  let optimalp = 0; // Placeholder
  let ove = 0; // Placeholder
  let tvo = 0; // Placeholder
  let tr = 0; // Placeholder

   // Add the core calculation logic here, simplified for structure:
   if (isOligopoly) {
     // Oligopoly calculations to find ox, op, etc.
   } else if (hasOrder) {
     // Order calculations to find ox, op, etc.
   } else {
       if (Math.abs(currentA) < 1e-9) {
           // Perfect Competition (a=0) calculation
           op = b;
           if (Math.abs(ag) > 1e-9) ox = (b - bg) / ag;
           else ox = (b >= bg) ? Infinity : 0;
           ox = Math.max(0, ox);
           if (hasKap) ox = Math.min(ox, kap);
           optimalx = ox; // Use the calculated ox
           optimalp = op;
           // Calculate other vars: tr, tvo, dbfinal, ove
           tr = op * ox;
           if (hasGskift && ox > gskift) {
             // Complex TVO calc
           } else {
             tvo = (0.5 * ag * ox + bg) * ox;
           }
           dbfinal = tr - tvo;
           ove = ox > 0 ? tvo / ox : bg;

       } else {
           // Standard Monopolistic Competition Calculation
           // Find ox where GROMS = GROMK
           let groms_a = 2 * currentA;
           let groms_b = b;
           // ... (logic to find intersection with GROMK1/GROMK2) ...
           // Simplified:
           if (Math.abs(groms_a - ag) > 1e-9) ox = (bg - groms_b) / (groms_a - ag);
           else ox = (groms_b >= bg) ? Infinity : 0;

           // ... (Handle GROMK2 if hasGskift) ...

           ox = Math.max(0, ox);
           if (hasKap) ox = Math.min(ox, kap);
           op = currentA * ox + b;
           optimalx = ox;
           optimalp = op;
           // Calculate other vars: tr, tvo, dbfinal, ove
           tr = op * ox;
           if (hasGskift && ox > gskift) {
              // Complex TVO calc
           } else {
               tvo = (0.5 * ag * ox + bg) * ox;
           }
            dbfinal = tr - tvo;
           ove = ox > 0 ? tvo / ox : bg;
       }
   }
   // Ensure final ox/op are valid numbers
   optimalx = isFinite(optimalx) ? optimalx : 0;
   optimalp = isFinite(optimalp) ? optimalp : 0;

  // --- Chart Options Calculation ---
  // Now calculate chartOptions based on the determined state and calculated optimal points
  let optionshighcharts = {}; // Use the name from your original code

  if (Math.abs(currentA) < 1e-9 && !isOligopoly && !hasOrder) { // Perfect Competition
      console.log("Adjusting chart options for Perfect Competition (a=0)");
      let maxYValue = Math.max(300, b * 1.5, bg * 1.5);
      let maxXValue = 200; 
      if (Math.abs(ag) > 1e-9) {
          let equilibriumX = (b - bg) / ag;
          if (equilibriumX > 0 && isFinite(equilibriumX)) {
              maxXValue = Math.max(200, equilibriumX * 2, optimalx * 1.5);
          }
      } else {
          maxXValue = Math.max(200, optimalx * 1.5);
      }
      if (hasKap) maxXValue = Math.max(maxXValue, kap * 1.2);

      optionshighcharts = {
          title: { text: 'Optimering - Fuldkommen Konkurrence' },
          credits: { enabled: false },
          xAxis: { min: 0, max: maxXValue, title: {text: 'Mængde'} },
          yAxis: { min: 0, max: maxYValue, title: {text: 'DKK'} },
          series: [
              { name: 'P', data: [[0, b], [maxXValue, b]], color: 'green', marker: { enabled: false } },
              { name: 'GROMS', data: [[0, b], [maxXValue, b]], color: 'blue', marker: { enabled: false } }, // Same as P
              { name: 'GROMK', data: [[0, bg], [maxXValue, ag * maxXValue + bg]], color: 'red', marker: { enabled: false } },
              // Add optimal point
              { name: 'Optimal', type: 'scatter', data: [[optimalx, optimalp]], color: 'black', zIndex: 5, marker:{radius: 5} },
              // Add capacity line if applicable
              ...(hasKap ? [{ name: 'Kapacitet', data: [[kap, 0], [kap, maxYValue]], color: 'orange', dashStyle: 'Dash', marker: { enabled: false }}] : [])
          ],
      };
  } else { 
      // Standard case or other scenarios (Oligopoly/Order logic needs to be added)
      console.log("Calculating standard chart options");
      let maxYValue = Math.max(300, b * 1.5, bg * 1.5, optimalp * 1.2);
      let maxXValue = 100;
      if (Math.abs(currentA) > 1e-9) {
        maxXValue = Math.max(100, (-b / currentA) * 1.5, optimalx * 1.5);
      } else { // Should not happen if a=0 case is handled above, but fallback
         maxXValue = Math.max(100, optimalx * 1.5); 
      }
      if (hasKap) maxXValue = Math.max(maxXValue, kap * 1.2);

      optionshighcharts = {
          title: { text: 'Optimering' },
          credits: { enabled: false },
          xAxis: { min: 0, max: maxXValue, title: {text: 'Mængde'} },
          yAxis: { min: 0, max: maxYValue, title: {text: 'DKK'} },
          series: [
               { name: 'P', data: [[0, b], [-b/currentA, 0]].filter(p => isFinite(p[0]) && isFinite(p[1])), color: 'green', marker: { enabled: false } },
               { name: 'GROMS', data: [[0, b], [-0.5*b/currentA, 0]].filter(p => isFinite(p[0]) && isFinite(p[1])), color: 'blue', marker: { enabled: false } },
               { name: 'GROMK', data: [[0, bg], [maxXValue, ag * maxXValue + bg]], color: 'red', marker: { enabled: false } },
               // Add GROMK2 segment if applicable
               ...(hasGskift ? [{ name: 'GROMK2', data: [[gskift, ag2 * gskift + bg2], [maxXValue, ag2 * maxXValue + bg2]], color: 'purple', marker: { enabled: false }}] : []),
               // Add optimal point
               { name: 'Optimal', type: 'scatter', data: [[optimalx, optimalp]], color: 'black', zIndex: 5, marker:{radius: 5} },
               // Add capacity line if applicable
               ...(hasKap ? [{ name: 'Kapacitet', data: [[kap, 0], [kap, maxYValue]], color: 'orange', dashStyle: 'Dash', marker: { enabled: false }}] : [])
           ],
      };
       // TODO: Add specific series logic for Oligopoly and Order scenarios here
  }

  // --- Original Calculation Variables (Ensure these are defined before use in JSX) ---
  // These seem to be intermediate calculation steps from your original code
  var gromkligning = numberFormat4(ag) + 'X+' + bg;
  var avcligning = numberFormat4(0.5 * ag) + 'X+' + bg;
  var gromkligningkort = ' = ' + numberFormat4(ag) + 'X+' + bg;
  var tvoligning2 = ' = ' + numberFormat4(0.5 * ag) + 'X\u00B2+' + bg + 'X';
  var outside1 = false; // Placeholder
  var outside2 = false; // Placeholder
  var skær2 = 0; // Placeholder


  // --- Return JSX --- 
  // Use the calculated optionshighcharts
  return (
    <div>
      <Container>
        <br></br>
        <div className="p-3 mb-2 bg-secondary text-light"> {/* Fixed className */} 
          <h3>Optimering</h3>
          <h5>Maksimering af dækningsbidrag</h5>
          <h5>Bestem optimal pris, afsætning og dækningsbidrag</h5>
        </div>
      </Container>

      <Container className="p-0">
        <div className="p-3 mb-2 bg-white text-black"> {/* Fixed className */} 
          <div className="card">
            <div className="card-body"></div>

            <Container>
              <div className="p-3 mb-2 mt-0 bg-white"> {/* Fixed className */} 
                {/* --- Toggle for Oligopoly --- */} 
                <Toggle
                  render={({ on, toggle }) => (
                    <div>
                      {on ? (
                        <>
                          {/* Oligopoly Inputs (P op/ned) */} 
                          <Form>
                               <h5>
                                 Prisforøgelse P<sub>op</sub> = a<sub>op</sub>X+b
                                 <sub>op</sub> = {aop}X+{bop}
                               </h5>
                               <Row>
                                 <Col>
                                   <InputGroup size="sm">
                                     <Form.Control
                                       type="number"
                                       step={0.01}
                                       value={aop}
                                       onChange={(e) => setaop(parseFloat(e.target.value) || 0)} // Use parseFloat
                                       aria-describedby="inputGroupAppend"
                                     />
                                     <InputGroup.Text id="basic-addon2"> {/* Removed InputGroup.Append */} 
                                       a<sub>op</sub>
                                     </InputGroup.Text>
                                   </InputGroup>
                                   <Form.Text className="text-muted">Hældningskoefficienten: a<sub>op</sub></Form.Text>
                                 </Col>
                                  <Col>
                                    <InputGroup size="sm">
                                      <Form.Control
                                        type="number"
                                        min="0"
                                        step={1}
                                        value={bop}
                                        onChange={(e) => setbop(Math.abs(parseFloat(e.target.value) || 0))} // Use parseFloat
                                        aria-describedby="inputGroupAppend"
                                      />
                                      <InputGroup.Text id="basic-addon2">b<sub>op</sub></InputGroup.Text>
                                    </InputGroup>
                                    <Form.Text className="text-muted">Skæringen med y-aksen: b<sub>op</sub></Form.Text>
                                  </Col>
                                </Row>
                                <h5>
                                  Prisnedsættelse P<sub>ned</sub> = a<sub>ned</sub>X+b<sub>ned</sub> = {aned}X+{bned}
                                </h5>
                                <Row>
                                   <Col>
                                     <InputGroup size="sm">
                                       <Form.Control
                                         type="number"
                                         step={0.01}
                                         value={aned}
                                         onChange={(e) => setaned(parseFloat(e.target.value) || 0)} // Use parseFloat
                                         aria-describedby="inputGroupAppend"
                                       />
                                       <InputGroup.Text id="basic-addon2">a<sub>ned</sub></InputGroup.Text>
                                     </InputGroup>
                                     <Form.Text className="text-muted">Hældningskoefficienten: a<sub>ned</sub></Form.Text>
                                   </Col>
                                   <Col>
                                     <InputGroup size="sm">
                                       <Form.Control
                                         type="number"
                                         min="0"
                                         step={1}
                                         value={bned}
                                         onChange={(e) => setbned(Math.abs(parseFloat(e.target.value) || 0))} // Use parseFloat
                                         aria-describedby="inputGroupAppend"
                                       />
                                       <InputGroup.Text id="basic-addon2">b<sub>ned</sub></InputGroup.Text>
                                     </InputGroup>
                                     <Form.Text className="text-muted">Skæringen med y-aksen: b<sub>ned</sub></Form.Text>
                                   </Col>
                                </Row>
                                <p className="mt-2 form-text">Beregnet knækpunkt: X = {numberFormat3(derivedKnækmængde)}, P = {numberFormat1(knækpris)}</p>
                           </Form>
                        </>
                      ) : (
                        <>
                          {/* Standard P Inputs */} 
                          <h5>Prisafsætningsfunktionen P = aX+b = {numberFormat4(currentA)}X+{b}</h5>
                           <Form>
                               <Row>
                                 <Col>
                                   <InputGroup size="sm">
                                     <FormControl
                                       type="number"
                                       max="-0.000000001"
                                       step={0.01}
                                       value={a} // Use original 'a' state here
                                       onChange={(e) => seta(-Math.abs(parseFloat(e.target.value) || 0))} // Update original 'a'
                                       placeholder="0"
                                     />
                                     <InputGroup.Text id="basic-addon2">a</InputGroup.Text>
                                   </InputGroup>
                                   <Form.Text className="text-muted">Hældningskoefficienten: a</Form.Text>
                                 </Col>
                                 <Col>
                                   <InputGroup size="sm">
                                     <Form.Control
                                       type="number"
                                       min="0"
                                       step={1}
                                       value={b}
                                       onChange={(e) => setb(Math.abs(parseFloat(e.target.value) || 0))}
                                       placeholder="0"
                                     />
                                     <InputGroup.Text id="basic-addon2">b</InputGroup.Text>
                                   </InputGroup>
                                   <Form.Text className="text-muted">Skæringen med y-aksen: b</Form.Text>
                                 </Col>
                               </Row>
                             </Form>
                        </>
                      )}
                      <Button size="sm" onClick={() => {
                          toggle();
                          // When toggling, update state variables that define the scenario
                          if (!on) { // Turning Oligopoly ON
                              setknækmængde(derivedKnækmængde); // Set calculated knæk
                              setordremængde(Infinity); // Turn off order
                              seta(-1); setb(100); // Reset standard P (might not be necessary)
                          } else { // Turning Oligopoly OFF
                              setknækmængde(Infinity);
                              // Reset oligopoly inputs? 
                              // setaop(-0.5); setbop(100); setaned(-1); setbned(125);
                          }
                      }}>
                        {on ? 'Fjern Oligopol' : 'Oligopol'}
                      </Button>
                    </div>
                  )}
                />

                {/* --- Toggle for Order (Only if NOT Oligopoly) --- */} 
                {!isOligopoly && (
                    <Toggle
                      render={({ on, toggle }) => (
                        <div>
                          {on && (
                             <>
                               {/* Order Inputs */} 
                               <h5>Ordrestørrelse på {ordre} stk. til pris {ordrepris} DKK.</h5>
                               <Form>
                               <Row>
                                 <Col>
                                   <InputGroup size="sm">
                                     <Form.Control
                                       type="number"
                                       step={1}
                                       value={ordre}
                                       onChange={(e) => setordre(Math.abs(parseFloat(e.target.value) || 0))}
                                     />
                                     <InputGroup.Text id="basic-addon31">stk.</InputGroup.Text>
                                   </InputGroup>
                                   <Form.Text className="text-muted">Ordrestørrelse i stk.</Form.Text>
                                 </Col>
                                 <Col>
                                   <InputGroup size="sm">
                                     <Form.Control
                                       type="number"
                                       min="0"
                                       step={1}
                                       value={ordrepris}
                                       onChange={(e) => setordrepris(Math.abs(parseFloat(e.target.value) || 0))}
                                     />
                                     <InputGroup.Text id="basic-addon2">DKK.</InputGroup.Text>
                                   </InputGroup>
                                   <Form.Text className="text-muted">Pris pr. stk.</Form.Text>
                                 </Col>
                               </Row>
                               </Form>
                            </>
                          )}
                           <Button size="sm" onClick={() => {
                              toggle();
                              if (!on) { // Turning Order ON
                                  setordremængde(ordre); // Use the state value 'ordre'
                                  setknækmængde(Infinity); // Turn off oligopoly
                              } else { // Turning Order OFF
                                  setordremængde(Infinity);
                                  // Reset order inputs?
                                  // setordre(20); setordrepris(64);
                              }
                           }}>
                             {on ? 'Fjern Ordre' : 'Ordre'}
                           </Button>
                         </div>
                       )}
                     />
                )}
                <br />

                {/* --- GROMK Inputs --- */} 
                <h5>
                  {gskift === +Infinity ? 'Grænseomkostningerne GROMK = aX+b = ' : 'Først GROMK\u2081 = a\u2081X+b\u2081 = '}
                  {numberFormat4(ag)}X+{numberFormat4(bg)} {/* Display GROMK1 */} 
                 </h5>
                 <Form>
                   <Row>
                      <Col>
                        <InputGroup size="sm">
                          <Form.Control
                            type="number"
                            step={0.01}
                            value={ag}
                            onChange={(e) => setag(parseFloat(e.target.value) || 0)}
                          />
                          <InputGroup.Text id="basic-addon2">a<sub>g1</sub></InputGroup.Text>
                        </InputGroup>
                        <Form.Text className="text-muted">Hældningskoefficienten: a<sub>g1</sub></Form.Text>
                      </Col>
                      <Col>
                        <InputGroup size="sm">
                          <Form.Control
                            type="number"
                            min="0"
                            step={1}
                            value={bg}
                            onChange={(e) => setbg(Math.abs(parseFloat(e.target.value) || 0))}
                          />
                          <InputGroup.Text id="basic-addon2">b<sub>g1</sub></InputGroup.Text>
                        </InputGroup>
                        <Form.Text className="text-muted">Skæringen med y-aksen: b<sub>g1</sub></Form.Text>
                      </Col>
                    </Row>
                 </Form>
                 <br />

                 {/* --- Toggle for GROMK2 --- */} 
                 <Toggle
                   render={({ on, toggle }) => (
                     <div>
                       {on && (
                         <>
                           <h5>Skift mellem GROMK funktioner</h5>
                           <Form>
                             <Row>
                               <Col sm="6">
                                 <InputGroup size="sm">
                                   <Form.Control
                                     type="number"
                                     min="0" 
                                     step={1}
                                     value={gskift === Infinity ? '' : gskift} // Handle Infinity display
                                     onChange={(e) => setgskift(parseFloat(e.target.value) || Infinity)} // Set to Infinity if empty/invalid
                                     placeholder="Ingen skift"
                                   />
                                   <InputGroup.Text id="basic-addon2">X</InputGroup.Text>
                                 </InputGroup>
                                 <Form.Text className="text-muted">X-værdi ved GROMK skift</Form.Text>
                               </Col>
                             </Row>
                           </Form>
                           <h5>Fortsat GROMK₂ = a₂X+b₂ = {numberFormat4(ag2)}X+{numberFormat4(bg2)}</h5>
                            <Form>
                             <Row>
                               <Col>
                                 <InputGroup size="sm">
                                   <Form.Control
                                     type="number"
                                     step={0.01}
                                     value={ag2}
                                     onChange={(e) => setag2(parseFloat(e.target.value) || 0)}
                                   />
                                   <InputGroup.Text id="basic-addon2">a<sub>2</sub></InputGroup.Text>
                                 </InputGroup>
                                 <Form.Text className="text-muted">Hældningskoefficienten: a<sub>2</sub></Form.Text>
                               </Col>
                               <Col>
                                 <InputGroup size="sm">
                                   <Form.Control
                                     type="number"
                                     min="0"
                                     step={1}
                                     value={bg2}
                                     onChange={(e) => setbg2(Math.abs(parseFloat(e.target.value) || 0))}
                                   />
                                   <InputGroup.Text id="basic-addon2">b<sub>2</sub></InputGroup.Text>
                                 </InputGroup>
                                 <Form.Text className="text-muted">Skæringen med y-aksen: b<sub>2</sub></Form.Text>
                               </Col>
                             </Row>
                           </Form>
                          </>
                        )}
                        <Button size="sm" onClick={() => {
                            toggle();
                            if (on) { // Turning OFF GROMK2
                                setgskift(Infinity);
                                setag2(0);
                                setbg2(0);
                            }
                         }}>
                          {on ? '1 GROMK funktion' : '2 GROMK funktioner'}
                        </Button>
                      </div>
                    )}
                  />

                 {/* --- Toggle for Capacity --- */} 
                 <Toggle
                   render={({ on, toggle }) => (
                     <div>
                       {on && (
                         <>
                           <h5>Kapacitetsbegrænsning</h5>
                           <Form>
                             <Row>
                               <Col sm="6">
                                 <InputGroup size="sm">
                                   <Form.Control
                                     type="number"
                                     min="0"
                                     step={1}
                                     value={kap === Infinity ? '' : kap} // Handle Infinity display
                                     onChange={(e) => setkap(parseFloat(e.target.value) || Infinity)}
                                     placeholder="Ingen grænse"
                                   />
                                   <InputGroup.Text id="basic-addon2">stk.</InputGroup.Text>
                                 </InputGroup>
                                 <Form.Text className="text-muted">Begrænset kapacitet i stk.</Form.Text>
                               </Col>
                             </Row>
                           </Form>
                         </>
                       )}
                       <Button size="sm" onClick={() => {
                           toggle();
                           if (on) { // Turning OFF Capacity limit
                               setkap(Infinity);
                           }
                       }}>
                         {on ? 'Ingen kapacitetsbegrænsning' : 'Kapacitetsbegrænsning'}
                       </Button>
                     </div>
                   )}
                 />

                 {/* --- Toggle for Advertising (Only if NOT Oligopoly/Order) --- */} 
                 {!isOligopoly && !hasOrder && (
                   <Toggle
                     render={({ on, toggle }) => (
                       <div>
                         {on && (
                           <>
                             <h5>Afsætningsændring i procent</h5>
                             <Form>
                               <Row>
                                 <Col sm="6">
                                   <InputGroup size="sm">
                                     <Form.Control
                                       type="number"
                                       min="-99" 
                                       step={1}
                                       value={reklame}
                                       onChange={(e) => setreklame(parseFloat(e.target.value) || 0)}
                                     />
                                     <InputGroup.Text id="basic-addon2">%</InputGroup.Text>
                                   </InputGroup>
                                   <Form.Text className="text-muted">
                                     Procentvis ændring i afsætning ved alle priser
                                   </Form.Text>
                                 </Col>
                               </Row>
                             </Form>
                           </>
                         )}
                         <Button size="sm" onClick={() => {
                             toggle();
                             if (on) { // Turning OFF advertising
                                 setreklame(0);
                             }
                         }}>
                           {on ? 'Ingen afsætningsændring' : 'Afsætningsændring'}
                         </Button>
                       </div>
                     )}
                   />
                 )}

              </div>
            </Container>
          </div>
        </div>
      </Container>

      <Container className="p-0">
        <div className="p-3 mb-2 bg-white text-black"> {/* Fixed className */} 
          <div className="card">
            <div className="card-body">
              <HighchartsReact
                highcharts={Highcharts}
                options={optionshighcharts} // Use the calculated options
              />
            </div>
          </div>
        </div>
      </Container>

      <Container className="p-0">
        <div className="container"> {/* Fixed className */} 
          <table className="table table-bordered table-responsive table-white table-hover table-striped "> {/* Fixed className */} 
            <small>
              <thead>
                <span className="align-middle">
                  <tr>
                    <br />
                    <h3>&nbsp;&nbsp;&nbsp;Forklaring på variable</h3>
                    <br />
                  </tr>
                </span>
              </thead>
              <tbody>
                <span className="align-top">
                  <tr>
                    <th>DK navn</th>
                    <th>US navn</th>
                    <th>Værdi</th>
                    <th>Forklaring</th>
                  </tr>
                  {/* --- Result Rows (Use calculated values) --- */} 
                  <tr><td>P</td><td>P</td><td>{/* Display P function based on scenario */}</td><td>{/* P Explanation */}</td></tr>
                  <tr><td>GROMS</td><td>MR</td><td>{/* Display GROMS function based on scenario */}</td><td>{/* GROMS Explanation */}</td></tr>
                  <tr><td>GROMK</td><td>MC</td><td>{/* Display GROMK function(s) */}</td><td>{/* GROMK Explanation */}</td></tr>
                  <tr><td>VE</td><td>AVC</td><td>{/* Display VE based on scenario */}</td><td>{/* VE Explanation */}</td></tr>
                  <tr><td>Omsætning</td><td>TR</td><td>{/* Display TR function */}</td><td>{/* TR Explanation */}</td></tr>
                  <tr><td>TVO</td><td>TVC</td><td>{/* Display TVO function */}</td><td>{/* TVO Explanation */}</td></tr>
                  <tr><td>X<sup>opt</sup></td><td>Q<sup>opt</sup></td><td>{numberFormat4(optimalx)} stk.</td><td>{/* Optimal X Explanation - Use calculationExplanation? */}</td></tr>
                  <tr><td>P<sup>opt</sup></td><td>P<sup>opt</sup></td><td>{numberFormat1(optimalp)}</td><td>{/* Optimal P Explanation */}</td></tr>
                  <tr><td>VE<sup>opt</sup></td><td>AVC<sup>opt</sup></td><td>{numberFormat1(ove)}</td><td>{/* Optimal VE Explanation */}</td></tr>
                  <tr><td>Omsætning<sup>opt</sup></td><td>TR<sup>opt</sup></td><td>{numberFormat1(tr)}</td><td>{/* Optimal TR Explanation */}</td></tr>
                  <tr><td>TVO<sup>opt</sup></td><td>TVC<sup>opt</sup></td><td>{numberFormat1(tvo)}</td><td>{/* Optimal TVO Explanation */}</td></tr>
                  <tr><td>DB<sup>opt</sup></td><td>Gross Profit<sup>opt</sup></td><td>{numberFormat1(dbfinal)}</td><td>{/* Optimal DB Explanation */}</td></tr>
                  <tr><td>e<sub>p</sub></td><td>e<sub>p</sub></td><td>{/* Display elasticity */}</td><td>{/* Elasticity Explanation */}</td></tr>
                </span>
              </tbody>
            </small>
          </table>
        </div>
      </Container>
    </div>
  );
}
